books = Book.create!([
  {title: "Best books", author: "Ryan Goslinnk", description: "A dude", year: nil, isbn: "85264815", cover_image_url: "", level: "", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "One Fish Two Fish Red Fish Blue Fish", author: "Dr. Seuss", description: "It's summertime, and the fishes are fifty...", year: nil, isbn: "9780394800134", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780394800134", level: "PreK-K", ar_level: nil, ar_points: nil, grl: "L", dra: "", is_bilingual: false, is_chapter: false},
  {title: "My Book", author: "Chester Arthur", description: "A tale as old as time", year: nil, isbn: "123456789", cover_image_url: "", level: "", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "A Family Mystery (Disney/Pixar Coco)", author: "Sarah Hernandez", description: "Despite his family's baffling generations-old ban on music, Miguel (voice of newcomer Anthony Gonzalez) dreams of becoming an accomplished musician like his idol, Ernesto de la Cruz (voice of Benjamin Bratt). Desperate to prove his talent, Miguel finds himself in the stunning and colorful Land of the Dead following a mysterious chain of events. Along the way, he meets charming trickster Hector (voice of Gael García Bernal), and together, they set off on an extraordinary journey to unlock the real story behind Miguel's family history. Directed by Lee Unkrich (Toy Story 3), co-directed by Adrian Molina (story artist Monsters University) and produced by Darla K. Anderson (Toy Story 3), Disney/Pixar's Coco opens in U.S. theaters on Nov. 22, 2017. Children ages 4 to 6 will love this Step 3 Step into Reading leveled reader with collector cards based on the film. Step 3 readers feature engaging characters in easy-to-follow plots about popular topics. For children who are ready to read on their own.", year: nil, isbn: "9780736438094", cover_image_url: "https://images.penguinrandomhouse.com/cover/9780736438094", level: "PreK-K", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "All About the Trolls (DreamWorks Trolls)", author: "Kristen L. Depken", description: "DreamWorks Animation's TROLLS is an irreverent comedy extravaganza with incredible music! From the genius creators of SHREK, TROLLS stars Anna Kendrick as Poppy, the optimistic leader of the Trolls, and her polar opposite, Branch, played by Justin Timberlake. \n Together, this unlikely pair of Trolls must embark on an adventure that takes them far beyond the only world they've ever known. This Step 3 Step into Reading leveled reader has sparkly glitter on the cover and over 30 stickers featuring Poppy, Branch, and the other Trolls--it's perfect for boys and girls ages 4 to 6!", year: nil, isbn: "9780399559037", cover_image_url: "https://images.penguinrandomhouse.com/cover/9780399559037", level: "PreK-K", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Ballerina Princess (Disney Princess)", author: "RH Disney", description: "Tutus and toe-shoes—what could be more fun? A Princess wearing them—of course! For the first time ever, the Disney Princesses become ballerinas. Cinderella, Belle, Snow White, and others will dance and spin themselves into the hearts of Princess fans in this sweet Step 2 reader.", year: nil, isbn: "9780736424288", cover_image_url: "https://images.penguinrandomhouse.com/cover/9780736424288", level: "G1-G2", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Baseball’s Greatest Hitters", author: "S.A. Kramer", description: "This Step 5 Sports Reader covers the greatest hitters in baseball: Williams, Ruth, Cobb, Aaron, and more. Now updated to include the Detroit Tigers's Miguel Cabrera, who made baseball history by achieving the Triple Crown in 2012, and up-and-comer Mike Trout of the Los Angeles Angels. This star-studded survey remains contemporary by addressing the Steroids Era, and how the use of Performance Enhancing Drugs has only served to disgrace some of baseball's top stars. The future of baseball is in a clean game--that'll determine who will achieve baseball greatness in the future.", year: nil, isbn: "9780553539103", cover_image_url: "https://images.penguinrandomhouse.com/cover/9780553539103", level: "G3-G5", ar_level: nil, ar_points: nil, grl: "P", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Basketball’s Greatest Players", author: "S.A. Kramer", description: "Young hoops fans can read about basketball's legendary, slam-dunking, hoop-hanging superstars in this Step 5 Sports Reader. Whose hands were so big he held the basketball like a grapefruit? Which center singlehandedly changed the way defense was played? From Wilt Chamberlain and Bill Russell, to Larry Bird and Michael Jordan, to the hottest new legends, such as LeBron James, readers will jump at the chance to learn about their favorite players!\n \n Step 5 Readers tell stories in chapters using longer paragraphs; for children who want to take the plunge into chapter books but still like colorful pictures.", year: nil, isbn: "9780553533941", cover_image_url: "https://images.penguinrandomhouse.com/cover/9780553533941", level: "G3-G5", ar_level: nil, ar_points: nil, grl: "R", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Big Bear, Little Bear (Disney/Pixar Brave)", author: "RH Disney", description: "Pixar Animation Studio's 13th animated feature film, Disney/Pixar Brave, is an epic adventure set in the rugged and mysterious Highlands of Scotland. Determined to carve her own path in life, a skilled archer named Princess Merida defies a sacred age-old custom--and inadvertently unleashes a beastly curse upon the kingdom. To set things right, Merida embarks on a perilous quest and discovers the meaning of true bravery. Kids ages 4-6 will love learning to read with this Step 1 reader based on the film.", year: nil, isbn: "9780736429153", cover_image_url: "https://images.penguinrandomhouse.com/cover/9780736429153", level: "PreK-K", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Big Dog, Little Dog", author: "P.D. Eastman", description: "Meet best friends Fred (Big Dog) and Ted (Little Dog) in P. D. Eastman’s classic Beginner Book. Though one is big and one is little, and one loves green and one loves red, these pup pals—along with their helpful acquaintance, Bird—prove that opposites can be the very best of friends.", year: nil, isbn: "9780394826691", cover_image_url: "https://images.penguinrandomhouse.com/cover/9780394826691", level: "PreK-K", ar_level: nil, ar_points: nil, grl: "I", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Charlie and the Chocolate Factory", author: "Roald Dahl", description: "Willy Wonka’s famous chocolate factory is opening at last! But only five lucky children will be allowed inside. And the winners are: Augustus Gloop, an enormously fat boy whose hobby is eating; Veruca Salt, a spoiled-rotten brat whose parents are wrapped around her little finger; Violet Beauregarde, a dim-witted gum-chewer with the fastest jaws around; Mike Teavee, a toy pistol-toting gangster-in-training who is obsessed with television; and Charlie Bucket, Our Hero, a boy who is honest and kind, brave and true, and good and ready for the wildest time of his life!", year: nil, isbn: "9780142410318", cover_image_url: "https://images.penguinrandomhouse.com/cover/9780142410318", level: "G3-G5", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Chase's Space Case (Paw Patrol)", author: "Kristen L. Depken", description: "Chase and the rest of Nickelodeon's PAW Patrol have an out-of-this-world adventure when they help a lost alien find his spaceship. Boys and girls ages 4 to 6 will love this Step 1 Step into Reading leveled reader with a shiny cover and two sheets of shiny stickers. \n \n Step 1 readers feature big type and easy words. Rhymes and rhythmic text paired with picture clues help children decode the story. This book is for children who know the alphabet and are eager to begin reading.", year: nil, isbn: "9780553538861", cover_image_url: "https://images.penguinrandomhouse.com/cover/9780553538861", level: "PreK-K", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Dancing in the Wings", author: "Debbie Allen", description: "Sassy is a long-legged girl who always has something to say. She wants to be a ballerina more than anything, but she worries that her too-large feet, too-long legs, and even her big mouth will keep her from her dream. When a famous director comes to visit her class, Sassy does her best to get his attention with her high jumps and bright leotard. Her first attempts are definitely not appreciated, but with Sassy’s persistence, she just might be able to win him over. Dancing in the Wings is loosely based on actress/choreographer Debbie Allen’s own experiences as a young dancer.", year: nil, isbn: "9780142501412", cover_image_url: "https://images.penguinrandomhouse.com/cover/9780142501412", level: "G1-G2", ar_level: "3.5", ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Dora Goes to School/Dora Va a la Escuela (Dora the Explorer)", author: "Random House", description: "Join Nickelodeon's Dora the Explorer and her best friend Boots as they help their teacher get to school on time. With English and Spanish text, this full-color storybook is perfecto for girls ages 3 to 7.", year: nil, isbn: "9780385374965", cover_image_url: "https://images.penguinrandomhouse.com/cover/9780385374965", level: "PreK-K", ar_level: nil, ar_points: nil, grl: "O", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Dragonbreath #1", author: "Ursula Vernon", description: "Danny Dragonbreath can’t breathe fire, but he has no fear. And that comes in handy when a bad grade at school inspires him to enlist his cousin the sea-serpent’s help with a research project. Using a hybrid of comic-book panels and text, Ursula Vernon introduces an irresistible set of characters that will have readers laughing until smoke comes out of their noses!", year: nil, isbn: "9780142420959", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780142420959", level: "G3-G5", ar_level: nil, ar_points: nil, grl: "O", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Dragonbreath #2: Attack of the Ninja Frogs", author: "Ursula Vernon", description: "Danny Dragonbreath knows girls are nothing but trouble. And the new foreign exchange student, Suki the Salamander, is the worst: she’s reduced his best friend, Wendell, to a blithering, lovesick tadpole. But when a group of ninja frogs attempt to kidnap her, Danny knows he must step in. Danny and Wendell have watched lots of kung fu movies and can totally take on a bunch of ninja frogs. Or, um, so he hopes . . .", year: nil, isbn: "9780142420669", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780142420669", level: "G3-G5", ar_level: nil, ar_points: nil, grl: "O", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Dragonbreath #3: Curse of the Were-wiener", author: "Ursula Vernon", description: "Danny Dragonbreath knows that school lunches can't be trusted. But even Danny is surprised when his hot dog bites his best friend, Wendell. Suddenly Wendell is having strange cravings, sprouting back hair, and itching like he's never itched before. Could Wendell be morphing into a . . . were-wiener?", year: nil, isbn: "9780147513212", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780147513212", level: "G3-G5", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Driving Buddies (Disney/Pixar Cars)", author: "Apple Jordan", description: "Lightning McQueen and Mater are as different as day and night--but they quickly learn that opposites attract when they become fast friends! Boys ages 4-6 will love this Disney/Pixar Cars Step into Reading reader based on the original film.", year: nil, isbn: "9780736423397", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780736423397", level: "PreK-K", ar_level: nil, ar_points: nil, grl: "O", dra: "", is_bilingual: false, is_chapter: false},
  {title: "EllRay Jakes Is Not a Chicken", author: "Sally Warner", description: "EllRay Jakes is tired of being bullied by fellow classmate Jared Matthews. But when EllRay tries to defend himself, he winds up in trouble. Then his dad offers him a deal: If he stays out of trouble for one week, they’ll go to Disneyland! EllRay says he can do it. But saying it and doing it are two very different things.", year: nil, isbn: "9780142419885", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780142419885", level: "G1-G2", ar_level: "4.8", ar_points: nil, grl: "J", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Green Eggs and Ham (Dr. Seuss)", author: "Dr. Seuss", description: "Do you like green eggs and ham? asks Sam-I-am in this Beginner Book by Dr. Seuss. In a house or with a mouse? In a boat or with a goat? On a train or in a tree? Sam keeps asking persistently. With unmistakable characters and signature rhymes, Dr. Seuss’s beloved favorite has cemented its place as a children’s classic. In this most famous of cumulative tales, the list of places to enjoy green eggs and ham, and friends to enjoy them with, gets longer and longer. Follow Sam-I-am as he insists that this unusual treat is indeed a delectable snack to be savored everywhere and in every way.", year: nil, isbn: "9780394800165", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780394800165", level: "PreK-K", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "High-Flying Skye (PAW Patrol)", author: "Random House", description: "Fans of Nickelodeon's PAW Patrol can meet Ryder and his awesome pups in this exciting book-and-CD format! This amazing storybook is perfect for kids ages 3 to 7--and the CD narration brings all the action to life!", year: nil, isbn: "9781101936863", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9781101936863", level: "G1-G2", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "I am Martin Luther King, Jr.", author: "Brad Meltzer", description: "Even as a child, Martin Luther King, Jr. was shocked by the terrible and unfair way African-American people were treated. When he grew up, he decided to do something about it--peacefully, with powerful words. He helped gather people together for nonviolent protests and marches, and he always spoke up about loving other human beings and doing what's right. He spoke about the dream of a kinder future, and bravely led the way toward racial equality in America. \n \n This lively, New York Times Bestselling biography series inspires kids to dream big, one great role model at a time. You'll want to collect each book.", year: nil, isbn: "9780525428527", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780525428527", level: "G1-G2", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "I am Rosa Parks", author: "Brad Meltzer", description: "Each picture book in this series is a biography of a significant historical figure, told in a simple, conversational, vivacious way, and always focusing on a character trait that made the person heroic. The heros are depicted as children throughout, telling their life stories in first-person present tense, which keeps the books playful and accessible to young children. And each book ends with a line of encouragement, a direct quote, and photos on the last page.\n \n Future biographies will include: Jackie Robinson, Albert Einstein, and Lucille Ball.", year: nil, isbn: "9780803740853", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780803740853", level: "G1-G2", ar_level: "3.1", ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "James and the Giant Peach", author: "Roald Dahl", description: "After James Henry Trotter’s parents are tragically eaten by a rhinoceros, he goes to live with his two horrible aunts, Spiker and Sponge. Life there is no fun, until James accidentally drops some magic crystals by the old peach tree and strange things start to happen. The peach at the top of the tree begins to grow, and before long it’s as big as a house. Inside, James meets a bunch of oversized friends—Grasshopper, Centipede, Ladybug, and more. With a snip of the stem, the peach starts rolling away, and the great adventure begins!", year: nil, isbn: "9780142410363", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780143106340", level: "G3-G5", ar_level: nil, ar_points: nil, grl: "M", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Junie B. Jones #10: Junie B. Jones Is a Party Animal", author: "Barbara Park", description: "Junie B. is spending the night! In the 10th Junie B. Jones book, Lucille is having a sleepover party--at her richie nanna's big giant house! Grace and Junie B. can't wait to see all the rich stuff in that place. Sleeping over at the nanna's is sure to be a dream come true! 'Cause what could possibly go wrong? With over 55 million books in print, Junie B. Jones is a classroom favorite and has been keeping kids laughing--and reading--for over 20 years!", year: nil, isbn: "9780679886631", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780679886631", level: "G1-G2", ar_level: nil, ar_points: nil, grl: "M", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Junie B. Jones #4: Junie B. Jones and Some Sneaky Peeky Spying", author: "Barbara Park", description: "In the 4th Junie B. Jones book, Junie B. is the bestest spier in the world. That's 'cause she has sneaky feet. And her nose doesn't whistle when she breathes. But guess what? Junie B. might be real sneaky. And real peeky. But when she spies on her teacher, Mrs., in the grocery store she could get into real trouble! With over 55 million books in print, Junie B. Jones is a classroom favorite and has been keeping kids laughing--and reading--for over 20 years!", year: nil, isbn: "9780679851011", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780679851011", level: "G1-G2", ar_level: nil, ar_points: nil, grl: "M", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Perro Grande Perro Pequeno / Big Dog Little Dog", author: "P.D. Eastman", description: "Meet best friends Fred (Big Dog) and Ted (Little Dog) in P. D. Eastman’s classic Beginner Book. Though one is big and one is little, and one loves green and one loves red, these pup pals—along with their helpful acquaintance, Bird—prove that opposites can be the very best of friends.", year: nil, isbn: "9780394851426", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780375986406", level: "PreK-K", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Junie B. Jones #9: Junie B. Jones Is Not a Crook", author: "Barbara Park", description: "In the 9th Junie B. Jones book, a terrible thing has happened to Junie B. Jones! And it's called--someone took her new black furry mittens! And they kept them! They didn't even put them in the Lost and Found at school. So when Junie B. finds a wonderful pen on the floor, she should be allowed to keep it, too. Right? That's fair. Right? Right? With over 55 million books in print, Junie B. Jones is a classroom favorite and has been keeping kids laughing--and reading--for over 20 years!", year: nil, isbn: "9780679883425", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780679883425", level: "G1-G2", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "La oruga muy hambrienta/The Very Hungry Caterpillar", author: "Eric Carle", description: "The time-honored classic The Very Hungry Caterpillar is now available in two bilingual formats, bringing together two languages for the ultimate reading and learning experience. With easily readable side-by-side English and Spanish text, it’s perfect for children learning to speak either language.\n \n La oruga muy hambrienta es un libro lleno de colorido que habla de como una oruga se transforma en una linda mariposa. Los niños se divierten al descrubrir los cambios tan asombrosos por los que pasa la oruga.", year: nil, isbn: "9780399256059", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780399256042", level: "PreK-K", ar_level: nil, ar_points: nil, grl: "G", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Llama Llama Red Pajama", author: "Anna Dewdney", description: "In this infectious rhyming read-aloud, Baby Llama turns bedtime into an all-out llama drama! Tucked into bed by his mama, Baby Llama immediately starts worrying when she goes downstairs, and his soft whimpers turn to hollers when she doesn’t come right back. But just in time, Mama returns to set things right. Children will relate to Baby Llama’s need for comfort, as much as parents will appreciate Mama Llama’s reassuring message.", year: nil, isbn: "9780670059836", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780451469908", level: "PreK-K", ar_level: "2.0", ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Marvin Redpost #1: Kidnapped at Birth?", author: "Louis Sachar", description: "There is one red post on the fence in Marvin Redpost's yard. He likes to touch it for luck on his way to school. But that doesn't mean he's a lucky kid! Quite the opposite. Marvin's imagination is always getting him into trouble! In this first book, Marvin hears a news report about a missing prince...a prince who matches his description exactly. Marvin has always wondered how he fits into his family. Maybe he really was kidnapped at birth...", year: nil, isbn: "9780679819462", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780679819462", level: "G1-G2", ar_level: nil, ar_points: nil, grl: "M", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Marvin Redpost #2: Why Pick on Me?", author: "Louis Sachar", description: "There's a rumor around school that Marvin Redpost picks his nose. Clarence started it and now everyone believes it. Even Marvin's friends are embarassed to be seen with him! Can Marvin figure out how to change everyone's mind about him? Or is he doomed to be teased forever?", year: nil, isbn: "9780679819479", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780679819479", level: "G1-G2", ar_level: nil, ar_points: nil, grl: "M", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Marvin Redpost #3: Is He a Girl?", author: "Louis Sachar", description: "When Casey Happleton tells Marvin that if you kiss your elbow you'll turn into a girl, Marvin doesn't believe a word of it. Then Marvin kisses his elbow by accident and starts to feel very strange. He wants pigtails and starts dotting his i's with little hearts. Could Casey be right? Or is it all in Marvin's head? You decide!", year: nil, isbn: "9780679819486", cover_image_url: "https://images.penguinrandomhouse.com/cover/9780679819486", level: "G1-G2", ar_level: nil, ar_points: nil, grl: "M", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Matilda", author: "Roald Dahl", description: "Matilda is a sweet, exceptional young girl, but her parents think she’s just a nuisance. She expects school to be different but there she has to face Miss Trunchbull, a kid-hating terror of a headmistress. When Matilda is attacked by the Trunchbull she suddenly discovers she has a remarkable power with which to fight back. It’ll take a superhuman genius to give Miss Trunchbull what she deserves and Matilda may be just the one to do it!", year: nil, isbn: "9780142410370", cover_image_url: "https://www.penguin.co.uk/content/dam/catalogue/pim/editions/336/9780141369365/cover.jpg", level: "G3-G5", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Meet the Pets (Secret Life of Pets)", author: "Mary Man-Kong", description: "Illumination Entertainment and Universal Pictures present The Secret Life of Pets, a comedy about the lives our pets lead after we leave for work or school each day. This Step 3 Step into Reading leveled reader has a shiny foil cover and collector cards, featuring the fun pets from the film. It's perfect for boys and girls ages 4 to 6!", year: nil, isbn: "9780399554834", cover_image_url: "https://images.penguinrandomhouse.com/cover/9780399554834", level: "PreK-K", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Mr. Pants: It's Go Time!", author: "Scott McCormick", description: "Meet Mr. Pants! It's the last day of summer, which means the last chance for fun, and he really, really wants to play laser tag. But his sisters have other ideas--the Fairy Princess Dream Factory? Gah! Good thing Mr. Pants knows how to get what he wants--and have a blast doing it.", year: nil, isbn: "9780147517104", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780147517104", level: "G1-G2", ar_level: "2.3", ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "National Geographic Kids Everything Robotics", author: "Jennifer Swanson", description: "They fix spacecraft, dance, tell jokes, and even clean your carpet! From the tiniest robo-bees to gigantic factory machines, robotics is all around you. This technology isn't just for science-fiction anymore -- it's real and more relevant than ever. With stunning visuals and energetic, impactful design, readers won't stop until they've learned everything there is to know about robotics.", year: nil, isbn: "9781426323317", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9781426323317", level: "G3-G5", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "National Geographic Readers: Cats vs. Dogs", author: "Elizabeth Carney", description: "Which are better, cats or dogs? Cats and dogs are the most popular pets with kids, and it seems whether you are a cat-lover, a dog-lover, neither, or both...sides must be taken on this hotly debated issue. Kids will love the stimulating, engaging text as they read and try to answer the question for themselves.", year: nil, isbn: "9781426307553", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9781426307553", level: "G1-G2", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "National Geographic Readers: Planets", author: "Elizabeth Carney", description: "This brilliantly illustrated book taps into children's natural curiosity about the vast world of space. This level two reader introduces children to all of the planets and dwarf planets in language that is easy for young readers to understand.", year: nil, isbn: "9781426310362", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9781426310362", level: "G1-G2", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "National Geographic Readers: Tigers", author: "Laura Marsh", description: "Tigers, perhaps the most majestic of all the big cats, are universally appealing to both boys and girls. This reader is illustrated with beautiful photographs to document their unique place in the wild and in the world. The carefully written text guarantees a successful and rewarding reading experience for kids who at Level 2 are reading on their own with occasional help.", year: nil, isbn: "9781426309113", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9781426309113", level: "G1-G2", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "National Geographic Readers: Weird Sea Creatures", author: "Laura Marsh", description: "Did you know that the deep-sea anglerfish has a glowing fishing rod attached to its body, or that the barreleye fish has a see-through head? See these wacky creatures and more in this brilliantly illustrated book that explores the strangest creatures under the sea. This level two reader uses easy-to-grasp language that will keep children intrigued and learning on every page.", year: nil, isbn: "9781426310478", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9781426310478", level: "G1-G2", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Skate This Way! (Shimmer and Shine)", author: "Random House", description: "Fans of Nickelodeon's Shimmer and Shine can meet the twin genies and all their friends in this exciting book-and-CD format! This amazing storybook is perfect for children ages 3 to 7--and the CD narration brings the magical adventure to life!", year: nil, isbn: "9781101938706", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9781101938706", level: "PreK-K", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Super Animals! (Disney Zootopia)", author: "Rico Green", description: "The modern mammal metropolis of Zootopia is a city like no other. Comprised of habitat neighborhoods like ritzy Sahara Square and frigid Tundratown, it's a melting pot where no matter what you are, from the biggest elephant to the smallest shrew, you can be anything. But when optimistic Officer Judy Hopps arrives, she discovers that being the first bunny on a police force of big, tough animals isn't so easy. Determined to prove herself, she jumps at the opportunity to crack a case, even if it means partnering with a fast-talking, scam-artist fox, Nick Wilde, to solve the mystery. Walt Disney Animation Studios' Zootopia opens in theaters on March 4, 2016. This Step 2 Step into Reading leveled reader based on Disney Zootopia features collector cards and a shiny cover!", year: nil, isbn: "9780736434546", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780736482080", level: "PreK-K", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Tales of a Fourth Grade Nothing", author: "Judy Blume", description: "Two is a crowd when Peter and his little brother, Fudge, are in the same room. Grown-ups think Fudge is absolutely adorable, but Peter and his pet turtle, Dribble, know the truth. From throwing temper tantrums to smearing mashed potatoes on the wall, Fudge causes mischief wherever he goes!", year: nil, isbn: "9780142408810", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780142408810", level: "G3-G5", ar_level: "3.3", ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "The Cat in the Hat (Dr. Seuss)", author: "Dr. Seuss", description: "Join the Cat in the Hat as he makes learning to read a joy! It’s a rainy day and Dick and Sally can’t find anything to do . . . until the Cat in the Hat unexpectedly appears and turns their dreary afternoon into a fun-filled extravaganza! This beloved Beginner Book by Dr. Seuss, which also features timeless Dr. Seuss characters such as Fish and Thing 1 and Thing 2, is fun to read aloud and easy to read alone.", year: nil, isbn: "9780394800011", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780394800011", level: "PreK-K", ar_level: nil, ar_points: nil, grl: "J", dra: "", is_bilingual: false, is_chapter: false},
  {title: "The Cat in the Hat / El Gato Ensombrerado", author: "Dr. Seuss", description: "This all-new rhymed bilingual edition of The Cat in the Hat is the perfect introduction for Spanish-speaking children to the wonderful world of Dr. Seuss. Now in a larger size trim to accommodate the English and the Spanish text, the new translation was written by Puerto Rican poet and children's book author Georgina Lazaro, and by Teresa Mlawer--one of the most respected figures in Spanish publishing. This new rhymed translation retains the fun of the original text, so that both Spanish-speakers and English-speakers can enjoy reading Dr. Seuss story in two languages.", year: nil, isbn: "9780553524437", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780553524437", level: "PreK-K", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "The Friendship Code #1", author: "Stacia Deutsch", description: "Lucy can't wait for the new coding club at school to start. Even though she's only in sixth grade, she has an idea for an app, and is excited to get started on it. But Lucy's excitement turns to disappointment when she shows up at the first meeting. Sophia is there--she and Lucy had a fight over the summer and they are now ex-friends. There's also Maya--a very cool seventh grader who Lucy's never dared talk to. And then there's Erin--a new girl who nobody really knows. Not only does Lucy not get to work on her brilliant idea for an app, she also ends up working with Sophia, Maya, and Erin to discover the meaning of some mysterious notes that are in coding language. Lucy and the rest of the girls soon discover that coding takes time, patience, and dedication--and so does friendship.", year: nil, isbn: "9780399542510", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780399542510", level: "G3-G5", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "The Little Mermaid Step into Reading (Disney Princess)", author: "Ruth Homberg", description: "Disney's The Little Mermaid comes to Diamond Edition DVD and Blu-ray in October 2013! Ariel is one of the most beloved Disney princesses of all time, and now girls ages 4-6 can relive the magic of the film with this Step 2 book.", year: nil, isbn: "9780736481281", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780736481281", level: "PreK-K", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "The Pups Save the Bunnies (Paw Patrol)", author: "Random House", description: "Nickelodeon's PAW Patrol hops to the rescue when Adventure Bay's bunnies need a new home. Boys and girls ages 3 to 7 will love this exciting full-color storybook, which features big flaps to lift.", year: nil, isbn: "9781101931684", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9781101931684", level: "PreK-K", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Welcome to Headquarters (Disney/Pixar Inside Out)", author: "RH Disney", description: "Disney/Pixar Inside Out takes you to a place that everyone knows but no one has ever seen: inside the human mind. This Step 3 Step into Reading leveled reader based on the film features a shiny foil cover and two sheets of sparkly stickers. Perfect for boys and girls ages 5 to 8! Disney/Pixar Inside Out releases in theaters June 19, 2015!\n \n Step 3 readers feature engaging characters in easy-to-follow plots about popular topics. For children who are ready to read on their own.", year: nil, isbn: "9780736433181", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780736433181", level: "G1-G2", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Wonder", author: "R.J. Palacio", description: "I won't describe what I look like. Whatever you're thinking, it's probably worse. \n August Pullman was born with a facial difference that, up until now, has prevented him from going to a mainstream school. Starting 5th grade at Beecher Prep, he wants nothing more than to be treated as an ordinary kid—but his new classmates can’t get past Auggie’s extraordinary face. WONDER, now a #1 New York Times bestseller and included on the Texas Bluebonnet Award master list, begins from Auggie’s point of view, but soon switches to include his classmates, his sister, her boyfriend, and others. These perspectives converge in a portrait of one community’s struggle with empathy, compassion, and acceptance. \n \n August (Auggie) Pullman was born with a facial deformity that prevented him from going to a mainstream school--until now. He's about to start 5th grade at Beecher Prep, and if you've ever been the new kid then you know how hard that can be. The thing is Auggie's just an ordinary kid, with an extraordinary face. But can he convince his new classmates that he's just like them, despite appearances?\n \n R. J. Palacio has written a spare, warm, uplifting story that will have readers laughing one minute and wiping away tears the next. With wonderfully realistic family interactions (flawed, but loving), lively school scenes, and short chapters, Wonder is accessible to readers of all levels.", year: nil, isbn: "9780375869020", cover_image_url: "http://images.penguinrandomhouse.com/cover/700jpg/9780375869020", level: "G3-G5", ar_level: nil, ar_points: nil, grl: "V", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Their book", author: "Jim Varney", description: "A man, a plan, a canal, panama", year: nil, isbn: "963852741", cover_image_url: "", level: nil, ar_level: nil, ar_points: nil, grl: nil, dra: nil, is_bilingual: false, is_chapter: false},
  {title: "Our book", author: "Ginger MaryAnne", description: "Lost on an island of mystery", year: nil, isbn: "41569812", cover_image_url: "", level: "", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Other books", author: "Franklin D", description: "My presidendcy", year: nil, isbn: "6548523615", cover_image_url: "", level: "", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false},
  {title: "Your Book", author: "Fred Rogers", description: "Put your shoes back on!", year: nil, isbn: "456789123", cover_image_url: "", level: "", ar_level: nil, ar_points: nil, grl: "", dra: "", is_bilingual: false, is_chapter: false}
])
